import React, { useState } from 'react';
import { Typography } from '@material-ui/core';
import Input from '@material-ui/core/Input';
import Button from '@material-ui/core/Button';
import API from './../utils/API';
import Book from './Book';

const Search = () => {

    let books

    const [bookSearch, setBookSearch] = useState("");

    const manageInput = e => {
        setBookSearch (e.target.value)
    }

    const googleBooksSearch = () => {
        API.titleSearch(bookSearch)
        .then(results => {
            books = results.items
            console.log(books)
        })
    }

    return (
        <Typography variant = "h6">
            <p>Search</p>
        <Input placeholder = "Title" onChange = {manageInput} value={bookSearch.title}/>
        <Button onClick = {googleBooksSearch}>Search</Button>
        <div>
            {books.map(book => (
                <Book title = {book.volumeInfo.title} author = {book.volumeInfo.author[0]} description = {book.volumeInfo.description} image = {book.volumeInfo.imageLinks.thumbnail} link = {book.volumeInfo.infoLink}/>
            ))}
        </div>
        
        </Typography>
    )
}

export default Search
